mapper.select:
SELECT COUNT(*) FROM AUCTIONITEM A JOIN CATEGORY C ON(A.CATEGORYNO = C.CATEGORYNO) 
JOIN AUCTIONDETAIL D ON (A.ITEMNO = D.ITEMNO)
WHERE C.UPPERCATEGORYNO =#{cateNO} AND D.STARTDAY BETWEEN #{datepicker1} AND #{datepicker2}

SELECT COUNT(*) FROM AUCTIONITEM A JOIN CATEGORY C ON(A.CATEGORYNO = C.CATEGORYNO) 
JOIN AUCTIONDETAIL D ON (A.ITEMNO = D.ITEMNO)
WHERE C.UPPERCATEGORYNO =#{cateNo} AND A.STARTPRICE BETWEEN 1000 AND 10000 AND D.STARTDAY BETWEEN #{datepicker1} AND #{datepicker2}

SELECT COUNT(*) FROM AUCTIONITEM A JOIN CATEGORY C ON(A.CATEGORYNO = C.CATEGORYNO) 
JOIN AUCTIONDETAIL D ON (A.ITEMNO = D.ITEMNO)
WHERE C.UPPERCATEGORYNO =#{cateNo} AND A.STARTPRICE BETWEEN 10001 AND 100000 AND D.STARTDAY BETWEEN #{datepicker1} AND #{datepicker2}

SELECT COUNT(*) FROM AUCTIONITEM A JOIN CATEGORY C ON(A.CATEGORYNO = C.CATEGORYNO) 
JOIN AUCTIONDETAIL D ON (A.ITEMNO = D.ITEMNO)
WHERE C.UPPERCATEGORYNO =#{cateNo} AND A.STARTPRICE BETWEEN 100001 AND 1000000 AND D.STARTDAY BETWEEN #{datepicker1} AND #{datepicker2}

SELECT COUNT(*) FROM AUCTIONITEM A JOIN CATEGORY C ON(A.CATEGORYNO = C.CATEGORYNO) 
JOIN AUCTIONDETAIL D ON (A.ITEMNO = D.ITEMNO)
WHERE C.UPPERCATEGORYNO =#{cateNo} AND A.STARTPRICE > 1000000 AND D.STARTDAY BETWEEN #{datepicker1} AND #{datepicker2}
